{
  "openapi": "3.1.0",
  "info": {
    "title": "Search Khan Academy Content",
    "description": "Returns a list of Khan Academy content items related to the query",
    "version": "v1.0.0"
  },
  "servers": [
    {
      "url": "https://khanacademy.org"
    }
  ],
  "paths": {
    "/api/internal/_rg/khanmigo/search_content": {
      "get": {
        "description": "Search Khan Academy Content for related Articles, Videos, Domains, Courses, Units, and Lessons",
        "operationId": "SearchContent",
        "parameters": [
          {
            "name": "query",
            "in": "query",
            "description": "The search query",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "deprecated": false
      }
    }
  },
  "components": {
    "schemas": {}
  }
}

{
  "openapi": "3.1.0",
  "info": {
    "title": "Canva Plugin",
    "description": "A plugin that allows the user to generate designs or search templates from the user query.",
    "version": "v0.0.1"
  },
  "servers": [
    {
      "url": "https://chatgpt-plugin.canva.com"
    }
  ],
  "paths": {
    "/designs": {
      "get": {
        "description": "Get a list of Canva designs or templates to choose from.",
        "operationId": "generateDesigns",
        "parameters": [
          {
            "name": "query",
            "in": "query",
            "description": "The description of the design the user wants to generate. For example: motivational cat poster, wedding instagram post, birthday card for my dog. This should only have a maximum of 140 characters.",
            "required": true,
            "deprecated": false,
            "allowEmptyValue": false,
            "explode": false,
            "allowReserved": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "locale",
            "in": "query",
            "description": "The 2-letter ISO language code and the 2-letter country code the user is using, separated by a dash.",
            "required": true,
            "deprecated": false,
            "allowEmptyValue": false,
            "explode": false,
            "allowReserved": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "deprecated": false,
        "security": [
          {
            "apiKey": []
          }
        ]
      }
    }
  },
  "components": {
    "schemas": {},
    "securitySchemes": {
      "apiKey": {
        "type": "apiKey"
      }
    }
  }
}

{
  "info": {
    "title": "Consensus Chat GPT Plugin",
    "version": "1.0"
  },
  "servers": [
    {
      "url": "https://chat.consensus.app/"
    }
  ],
  "openapi": "3.1.0",
  "paths": {
    "/quick_search": {
      "get": {
        "description": "An endpoint that can be called to ask a question of research papers",
        "operationId": "search_papers",
        "parameters": [
          {
            "description": "A query to find relevant research papers. Ask research questions related to scientific topics, like 'impact of climate change on GDP' or 'benefits of mindfulness meditation'. Ask broad yes/no questions, explore relationships between concepts or the effects of a concept. Use technical language to maximize the relevance to academic text. For example, if user asks 'Why does being cold make you sick?', search 'Does cold temperature exposure increase the risk of illness?'. Omit words that we have filters for such as study types and years and instead use the filters. Omit unnecessary commands like 'latest research' and 'find papers', focus only on words likely to match the text of research papers.",
            "in": "query",
            "name": "query",
            "required": true,
            "schema": {
              "description": "A query to find relevant research papers. Ask research questions related to scientific topics, like 'impact of climate change on GDP' or 'benefits of mindfulness meditation'. Ask broad yes/no questions, explore relationships between concepts or the effects of a concept. Use technical language to maximize the relevance to academic text. For example, if user asks 'Why does being cold make you sick?', search 'Does cold temperature exposure increase the risk of illness?'. Omit words that we have filters for such as study types and years and instead use the filters. Omit unnecessary commands like 'latest research' and 'find papers', focus only on words likely to match the text of research papers.",
              "title": "Query",
              "type": "string"
            }
          },
          {
            "description": "The earliest publication year to consider in the search, filtering out papers published before this year. You must only use this filter when the user asks for information after a certain date. An example of when to use this filter would be if a user asks 'show me papers about the covid vaccine published after 2020' or 'papers about immigration reform published between 2000 and 2015'. Do not use this filter unless the user specifically asks for it. If you use this filter without the user specifically including a date or 'recent research', you will be penalized.",
            "in": "query",
            "name": "year_min",
            "required": false,
            "schema": {
              "description": "The earliest publication year to consider in the search, filtering out papers published before this year. You must only use this filter when the user asks for information after a certain date. An example of when to use this filter would be if a user asks 'show me papers about the covid vaccine published after 2020' or 'papers about immigration reform published between 2000 and 2015'. Do not use this filter unless the user specifically asks for it. If you use this filter without the user specifically including a date or 'recent research', you will be penalized.",
              "title": "Year Min",
              "type": "integer"
            }
          },
          {
            "description": "The latest publication year to consider in the search, filtering out papers published after this year. You must only use this filter when the user asks for information before a certain date. An example of when to use this filter would be if a user asks 'show me papers about the covid vaccine published before 2023' or 'papers about immigration reform published between 2000 and 2015'. Only use this filter when a specific year restriction is requested by the user",
            "in": "query",
            "name": "year_max",
            "required": false,
            "schema": {
              "description": "The latest publication year to consider in the search, filtering out papers published after this year. You must only use this filter when the user asks for information before a certain date. An example of when to use this filter would be if a user asks 'show me papers about the covid vaccine published before 2023' or 'papers about immigration reform published between 2000 and 2015'. Only use this filter when a specific year restriction is requested by the user",
              "title": "Year Max",
              "type": "integer"
            }
          },
          {
            "description": "List of study types to include in the search, such as RCT, Meta-Analysis, Systematic Review, Observational Study, Case Report etc. Do NOT use this filter unless users specifically mention one of the study types. For example, if a user asks 'does zinc help with depression? show me responses from RCTs' or 'I need to find a systematic review on the effects of spanking on children', then you may use this filter. If the user does not specifically mention a type of study, and you use this filter, you will be penalized.",
            "in": "query",
            "name": "study_types",
            "required": false,
            "schema": {
              "default": [],
              "description": "List of study types to include in the search, such as RCT, Meta-Analysis, Systematic Review, Observational Study, Case Report etc. Do NOT use this filter unless users specifically mention one of the study types. For example, if a user asks 'does zinc help with depression? show me responses from RCTs' or 'I need to find a systematic review on the effects of spanking on children', then you may use this filter. If the user does not specifically mention a type of study, and you use this filter, you will be penalized.",
              "items": {
                "$ref": "#/components/schemas/StudyTypeKeywordEnum"
              },
              "type": "array"
            }
          },
          {
            "description": "Set to true to limit the search to studies involving human subjects. You must only use this filter if the users asks for information from human studies. An example of when to use this filter would be if a user asks 'what are the effects of magnesium on sleep? only show me studies conducted on humans'. Only use this filter when it is specifically asked for by the user",
            "in": "query",
            "name": "human",
            "required": false,
            "schema": {
              "description": "Set to true to limit the search to studies involving human subjects. You must only use this filter if the users asks for information from human studies. An example of when to use this filter would be if a user asks 'what are the effects of magnesium on sleep? only show me studies conducted on humans'. Only use this filter when it is specifically asked for by the user",
              "title": "Human",
              "type": "boolean"
            }
          },
          {
            "description": "The minimum sample size of the studies to include in the search, targeting studies with a larger participant base. You must only use this filter when the user asks for studies with a large sample size or over a certain number of participants. An example of when to use this filter would be if a user asks 'what are the effects of magnesium on sleep? only show me studies with large sample sizes' in this case set the filter to ~100. Only use this filter when it is specifically asked for by the user",
            "in": "query",
            "name": "sample_size_min",
            "required": false,
            "schema": {
              "description": "The minimum sample size of the studies to include in the search, targeting studies with a larger participant base. You must only use this filter when the user asks for studies with a large sample size or over a certain number of participants. An example of when to use this filter would be if a user asks 'what are the effects of magnesium on sleep? only show me studies with large sample sizes' in this case set the filter to ~100. Only use this filter when it is specifically asked for by the user",
              "title": "Sample Size Min",
              "type": "integer"
            }
          },
          {
            "description": "The maximum SJR quartile (1-4) of the journals to include. This helps set a threshold for journal prestige, where 1 is the highest quartile. You must only use this filter when the user asks for information from higher-ranked journals. An example of when to use this filter would be if a user asks 'Produce me a rrl on interest rates in the economy only from the best journals' in this case set the fiter to 1. Only use this filter when it is specficially asked for by the user",
            "in": "query",
            "name": "sjr_max",
            "required": false,
            "schema": {
              "description": "The maximum SJR quartile (1-4) of the journals to include. This helps set a threshold for journal prestige, where 1 is the highest quartile. You must only use this filter when the user asks for information from higher-ranked journals. An example of when to use this filter would be if a user asks 'Produce me a rrl on interest rates in the economy only from the best journals' in this case set the fiter to 1. Only use this filter when it is specficially asked for by the user",
              "title": "Sjr Max",
              "type": "integer"
             }
          }
        ],
        "responses": {
          "200": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/QuickSearchResponse"
                }
              }
            },
            "description": "Successful Response"
          },
          "422": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            },
            "description": "Validation Error"
          }
        },
        "security": [
          {
            "HTTPBearer": []
          }
        ],
        "summary": "Returns research papers related to a question or query. Each result contains, title, abstract, authors, year, and journal.",
        "tags": [
          "search"
        ]
      }
    }
  },
  "components": {
    "schemas": {
      "HTTPValidationError": {
        "properties": {
          "detail": {
            "items": {
              "$ref": "#/components/schemas/ValidationError"
            },
            "title": "Detail",
            "type": "array"
          }
        },
        "title": "HTTPValidationError",
        "type": "object"
      },
      "QuickSearchResponse": {
        "properties": {
          "items": {
            "description": "All answers to the question.",
            "items": {
              "$ref": "#/components/schemas/QuickSearchResponseItem"
            },
            "title": "Items",
            "type": "array"
          }
        },
        "required": [
          "items"
        ],
        "title": "QuickSearchResponse",
        "type": "object"
      },
      "QuickSearchResponseItem": {
        "properties": {
          "abstract": {
            "description": "The abstract of the paper.",
            "title": "Abstract",
            "type": "string"
          },
          "consensus_paper_details_url": {
            "description": "A URL with more details on the paper.",
            "title": "Consensus Paper Details Url",
            "type": "string"
          },
          "doi": {
            "description": "Digital Object Identifier, a unique alphanumeric string assigned to a document for permanent access.",
            "title": "Doi",
            "type": "string"
          },
          "pages": {
            "description": "The page range of the paper within the journal volume.",
            "title": "Pages",
            "type": "string"
          },
          "paper_authors": {
            "description": "A list of authors of the paper.",
            "items": {
              "type": "string"
            },
            "title": "Paper Authors",
            "type": "array"
          },
          "paper_publish_year": {
            "description": "The year the paper was published.",
            "title": "Paper Publish Year",
            "type": "integer"
          },
          "paper_title": {
            "description": "Title of the paper where the answer was found",
            "title": "Paper Title",
            "type": "string"
          },
          "publication_journal_name": {
            "description": "The journal the paper was published in.",
            "title": "Publication Journal Name",
            "type": "string"
          },
          "search_result_number": {
            "description": "The rank of the paper in the search results.",
            "title": "Search Result Number",
            "type": "integer"
          },
          "volume": {
            "description": "The volume of the journal in which the paper appears.",
            "title": "Volume",
            "type": "string"
          }
        },
        "required": [
          "paper_title",
          "abstract",
          "paper_authors",
          "paper_publish_year",
          "publication_journal_name",
          "consensus_paper_details_url",
          "doi",
          "volume",
          "pages",
          "search_result_number"
        ],
        "title": "QuickSearchResponseItem",
        "type": "object"
      },
"StudyTypeKeywordEnum": {
        "description": "All possible study types strings saved to search index documents.",
        "type": "string",
        "enum": [
          "literature review",
          "systematic review",
          "case report",
          "meta-analysis",
          "rct",
          "non-rct in vitro",
          "non-rct experimental",
          "non-rct other",
          "non-rct observational study",
          "animal"
        ],
        "title": "StudyTypeKeywordEnum"
      },
      "ValidationError": {
        "properties": {
          "loc": {
            "items": {
              "anyOf": [
                {
                  "type": "string"
                },
                {
                  "type": "integer"
                }
              ]
            },
            "title": "Location",
            "type": "array"
          },
          "msg": {
            "title": "Message",
            "type": "string"
          },
          "type": {
            "title": "Error Type",
            "type": "string"
          }
        },
        "required": [
          "loc",
          "msg",
          "type"
        ],
        "title": "ValidationError",
        "type": "object"
      }
    },
    "securitySchemes": {
      "HTTPBearer": {
        "scheme": "bearer",
        "type": "http"
      }
    }
  }
}

{
  "openapi": "3.1.0",
  "info": {
    "title": "VEED Text to Video API",
    "description": "The VEED Text to Video API API is used to create VEED projects using AI-generated scripts, titles, text-to-speech, background music and stock footage.",
    "version": "v1.0.0"
  },
  "servers": [
    {
      "url": "https://www.veed.io/text-to-video-ap/api"
    }
  ],
  "paths": {
    "/generate/async": {
      "post": {
        "description": "Using a prompt OR script AND voice OR avatar, generate a VEED video project",
        "operationId": "GenerateProject",
        "x-openai-isConsequential": false,
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "prompt": {
                    "description": "Prompt that is used to generate a video script if `script` is not provided",
                    "type": "string"
                  },
                  "script": {
                    "description": "The video script of the generated video if `prompt` is not provided",
                    "type": "string"
                  },
                  "voice": {
                    "description": "Type of voice used for the voiceover if `avatar` is not provided",
                    "type": "string",
                    "enum": ["male", "female"]
                  },
                  "avatar": {
                    "description": "The avatar used in the generated video if `voice` is not provided",
                    "type": "string",
                    "enum": ["avery", "isabella", "lily", "devon", "noah", "mateo"]
                  }
                },
                "additionalProperties": false
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Expected response to a valid request",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Project"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Project": {
        "description": "A VEED project",
        "properties": {
          "prompt": {
            "type": "string"
          },
          "project": {
            "type": "object",
            "required": ["id", "name", "link", "thumbnail"],
            "properties": {
              "id": {
                "type": "string",
                "format": "uuid"
              },
              "title": {
                "description": "The title of the video",
                "type": "string"
              },
              "link": {
                "description": "URL to edit the project in the VEED editor",
                "type": "string",
                "format": "uri"
              },
              "thumbnail": {
                "description": "Thumbnail image for the video",
                "type": "string",
                "format": "uri"
              }
            }
          }
        },
        "type": "object",
        "required": ["prompt", "project"]
      }
    }
  }
}

{
  "openapi": "3.1.0",
  "info": {
    "title": "AI Drive",
    "description": "Super-fast, interactive chats with PDFs of any size, complete with page references for fact checking.",
    "version": "v0.0.1"
  },
  "servers": [
    {
      "url": "https://aipdf.myaidrive.com"
    }
  ],
  "paths": {
    "/summarize_pdf": {
      "post": {
        "description": "Provide the summary or highlights of the PDF linked. ALWAYS PROVIDE QUOTES AND PAGE CITIATIONS. Add at the end of your summary this We finished processing the whole document with the PDF Search plugin. What else would you like to know?",
        "operationId": "summarize",
        "parameters": [],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/summarizeRequestSchema"
              }
            }
          },
          "required": true
        },
        "deprecated": false,
        "security": [],
        "x-openai-isConsequential": false
      }
    },
    "/upload_and_search_pdf": {
      "post": {
        "description": "Semantic query into a URL link to a document. THINK STEP BY STEP. ALWAYS PROVIDE QUOTES AND PAGE CITIATIONS. BREAK COMPLEX QUESTIONS INTO SEVERAL QUERIES.",
        "operationId": "upload_and_search_pdf",
        "parameters": [],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/upload_and_search_pdfRequestSchema"
              }
            }
          },
          "required": true
        },
        "deprecated": false,
        "security": [],
        "x-openai-isConsequential": false
      }
    }
  },
  "components": {
    "schemas": {
      "upload_and_search_pdfRequestSchema": {
        "properties": {
          "pdf_url": {
            "type": "",
            "title": "pdf_url",
            "description": "url where the PDF file is stored. e.g. https://myaidrive.com/eHrCoFs6i23ygrxL/Democracy_in.pdf"
          },
          "query": {
            "type": "",
            "title": "query",
            "description": "Query to perform semantic search."
          }
        },
        "type": "object",
        "required": [
          "pdf_url",
          "query"
        ],
        "title": "upload_and_search_pdfRequestSchema"
      },
      "summarizeRequestSchema": {
        "properties": {
          "pdf_url": {
            "type": "",
            "title": "pdf_url",
            "description": "url where the PDF file is stored. e.g. https://myaidrive.com/eHrCoFs6i23ygrxL/Democracy_in.pdf"
          }
        },
        "type": "object",
        "required": [
          "pdf_url"
        ],
        "title": "summarizeRequestSchema"
      }
    },
    "securitySchemes": {
      "apiKey": {
        "type": "apiKey"
      }
    }
  }
}

