            "type": "string",
            "title": "Url"
          }
        }
      },
      "VideoResponse": {
        "type": "object",
        "title": "VideoResponse",
        "required": [
          "title",
          "thumbnail_url",
          "token_count",
          "content",
          "assistant_hint"
        ],
        "properties": {
          "title": {
            "type": "string",
            "description": "The title of the video"
          },
          "thumbnail_url": {
            "type": "string",
            "format": "uri",
            "description": "The URL of the video thumbnail"
          },
          "token_count": {
            "type": "integer",
            "description": "The token count"
          },
          "content": {
            "oneOf": [
              {
                "$ref": "#/components/schemas/Transcript"
              },
              {
                "$ref": "#/components/schemas/Summary"
              }
            ]
          },
          "assistant_hint": {
            "type": "string",
            "description": "The instructions to follow"
          }
        }
      },
      "NotesResponse": {
        "type": "object",
        "title": "NotesResponse",
        "required": [
          "title",
          "thumbnail_url",
          "token_count",
          "assistant_hint",
          "content"
        ],
        "properties": {
          "title": {
            "type": "string",
            "description": "The title of the video"
          },
          "thumbnail_url": {
            "type": "string",
            "format": "uri",
            "description": "The URL of the video thumbnail"
          },
          "token_count": {
            "type": "integer",
            "description": "The token count of the content"
          },
          "assistant_hint": {
            "type": "string",
            "description": "The instructions to follow"
          },
          "content": {
            "oneOf": [
              {
                "$ref": "#/components/schemas/Transcript"
              },
              {
                "$ref": "#/components/schemas/Notes"
              }
            ]
          }
        }
      },
      "Transcript": {
        "type": "string",
        "description": "The full transcript of the video"
      },
      "Notes": {
        "type": "string",
        "description": "The generated notes for the video"
      },
      "Summary": {
        "type": "string",
        "description": "The generated summary of the video"
      },
      "TextModel": {
        "type": "object",
        "title": "TextModel",
        "required": [
          "text"
        ],
        "properties": {
          "text": {
            "type": "string",
            "description": "Markdown text to be converted to PDF",
            "example": "## This is a title\nThis is some text."
          }
        }
      },
      "SharedLinkResponse": {
        "type": "object",
        "title": "SharedLinkResponse",
        "required": [
          "link"
        ],
        "properties": {
          "link": {
            "type": "string",
            "format": "uri",
            "description": "Shareable Dropbox link of the uploaded PDF"
          }
        }
      },
      "ChunkResponse": {
        "type": "object",
        "properties": {
          "unique_id": {
            "type": "string",
            "description": "The unique ID associated with the video transcript"
          },
          "video_title": {
            "type": "string",
            "description": "The title of the video"
          },
          "current_chunk_index": {
            "type": "integer",
            "description": "The index of the current chunk"
          },
          "next_chunk_to_fetch": {
            "type": "integer",
            "description": "The index of the next chunk to fetch"
          },
          "Chunks_left_to_fetch": {
            "type": "integer",
            "description": "The number of chunks left to fetch"
          },
          "chunk": {
            "type": "object",
            "description": "The content of the current chunk"
          }
        }
      },
      "Article": {
        "type": "object",
        "properties": {
          "url": {
            "type": "string",
            "format": "uri"
          }
        }
      },
      "SuccessfulArticleResponse": {
        "type": "object",
        "properties": {
          "article_content": {
            "type": "string"
          },
          "youtube_links": {
            "type": "array",
            "items": {
              "type": "string"
            }
          },
          "source": {
            "type": "string",
            "format": "uri"
          },
          "assistant_hint": {
            "type": "string"
          }
        }
      },
      "UnavailableArticleError": {
        "type": "object",
        "properties": {
          "UNAVAILABLE_ARTICLE_ERROR": {
            "type": "string"
          }
        }
      },
      "ProcessingError": {
        "type": "object",
        "properties": {
          "PROCESSING_ERROR": {
            "type": "string"
          }
        }
      },
      "ArticleTooLongError": {
        "type": "object",
        "properties": {
          "ARTICLE_TOO_LONG": {
            "type": "string"
          }
        }
      }
    }
  },
